import React, { useState } from "react";
import Card from "./Card";
import axios from "axios";

const Main = () => {
    const [search, setSearch] = useState("");
    const [bookData, setData] = useState([]);
    const [newBook, setNewBook] = useState({ title: "", author: "", image: "", price: "" });
    const [showForm, setShowForm] = useState(false);

    const searchBook = (evt) => {
        if (evt.key === "Enter") {
            axios.get('https://www.googleapis.com/books/v1/volumes?q=' + search + '&key=AIzaSyA6SaT23KNiiA6DnUfUQTvFeyAcQEkwnSU' + '&maxResults=40')
                .then(res => setData(res.data.items))
                .catch(err => console.log(err));
        }
    };

    const handleAddBook = () => {
        // Burada yeni kitap ekleme işlemini gerçekleştirin
        // Örneğin, aşağıdaki kod, yeni kitap verilerini mevcut kitap listesine ekler
        setData([...bookData, {
            volumeInfo: {
                title: newBook.title,
                authors: [newBook.author],
                imageLinks: { smallThumbnail: newBook.image },
                publisher: "Unknown",
                publishedDate: "Unknown",
                description: "No description available"
            },
            saleInfo: {
                listPrice: { amount: newBook.price }
            }
        }]);
        setNewBook({ title: "", author: "", image: "", price: "" });
        setShowForm(false);
    };

    return (
        <>
            <div className="header">
                <div className="row1">
                    <h1>A room without books is like<br /> a body without a soul.</h1>
                </div>
                <div className="row2">
                    <h2>Find Your Book</h2>
                    <div className="search">
                        <input
                            type="text"
                            placeholder="Enter Your Book Name"
                            value={search}
                            onChange={e => setSearch(e.target.value)}
                            onKeyPress={searchBook}
                        />
                        <button><i className="fas fa-search"></i></button>
                    </div>
                    <img src="./images/bg2.png" alt="" />
                </div>
            </div>

            <div className="container">
                <Card book={bookData} />
            </div>

            <button onClick={() => setShowForm(!showForm)}>
                {showForm ? "Cancel" : "Add New Book"}
            </button>

            {showForm && (
                <div className="form-container">
                    <h3>Add New Book</h3>
                    <input
                        type="text"
                        placeholder="Title"
                        value={newBook.title}
                        onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Author"
                        value={newBook.author}
                        onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Image URL"
                        value={newBook.image}
                        onChange={(e) => setNewBook({ ...newBook, image: e.target.value })}
                    />
                    <input
                        type="text"
                        placeholder="Price"
                        value={newBook.price}
                        onChange={(e) => setNewBook({ ...newBook, price: e.target.value })}
                    />
                    <button onClick={handleAddBook}>Add Book</button>
                </div>
            )}
        </>
    );
};

export default Main;
